import React from 'react';
class Audio  extends React.Component{
    state = {
        playing: this.props.songdata.playing,
        track: this.props.songdata.currentTrack
    }
    componentDidUpdate(){
        this.player.src = this.state.track;

    }
    render(){
        return(
            <div className="audiosec">
                {
                    /*<audio controls>  
                        <source src={this.state.track} type="audio/mpeg" />  
                    </audio>*/
                }
                <audio ref={ref => this.player = ref} />
            </div>
        )
    }
}
export default Audio;
