import React from 'react';
//import Audio from './Audio';
import '../App.css';
let flag = false;
let max = 9, min = 1;
class Main extends React.Component{
    state = {
        playing: false,
        currentTrack: '',
        currentImage: '',
        currentId: 1
    }
    componentDidUpdate(){
        if(this.state.currentTrack) {
            this.player.src = this.state.currentTrack;
            this.player.play()
        }
        if(this.state.playing){
            if(this.state.currentTrack === ''){
                this.player.src = this.props.musicdata[0].file;
                this.player.play();
            }else{
            this.player.src = this.state.currentTrack;
            this.player.play();
            }
        }else{
            this.player.pause();
        }
    }
    songHandler = (id) => {
        let currentSong = '';
        flag = true;
        if(this.props.musicdata.length > 0){
            for(let i = 0; i < this.props.musicdata.length; i++){
                if(id === this.props.musicdata[i].id){
                    currentSong = this.props.musicdata[i].file;
                    this.setState({
                        playing:true,
                        currentTrack: currentSong,
                        currentImage: this.props.musicdata[i].albumCover,
                        currentId: this.props.musicdata[i].id
                    });
                }
            }
        }
    }
    shuffleHandler(){
        //console.log('shuffle clicked');
        flag = true;
        let random = Math.floor(Math.random() * (+max - +min)) + +min;
        console.log(random);
        if(this.props.musicdata.length > 0){
            this.setState({
                playing: true,
                currentTrack: this.props.musicdata[random].file,
                currentImage: this.props.musicdata[random].albumCover,
                currentId: this.props.musicdata[random].id
            })
        }
    }
    prevsongHandler = () => {
        //console.log('prevsong clicked');
        if(this.props.musicdata.length > 0){
            if(this.state.currentId > 1){
                this.setState((prevState) => ({
                    currentId: prevState.currentId - 1
                }));
            }else if(this.state.currentId === 1){
                this.setState({
                    currentId: 8
                });
            } 
            this.setState({
                playing: true,
                currentTrack: this.props.musicdata[this.state.currentId].file,
                currentImage: this.props.musicdata[this.state.currentId].albumCover
            })  
        }
    }
    playpauseHandler = () => {
        this.setState((prevState) => ({
            playing: !prevState.playing
        }));
    }
    nextsongHandler = () => {
        console.log('nextsong clicked');
    }
    loopHandler = () => {
        console.log('loop clicked');
    }
    render(){
        console.log(this.state.playing);
        console.log(this.state.currentId);
        return(
            <div className="container">
                <div className="leftSide">
                    <div className="bigimagesec">
                      { flag === true ? <img src = {this.state.currentImage} alt='' className="bigimage"/> : <img src = {this.props.musicdata.length>0?this.props.musicdata[0].albumCover:''} alt='' className="bigimage"/> }
                    </div>
                    <div className="audiosection">
                        <audio ref={ref => this.player = ref} src="#"/>
                        <div className="controllersec">

                            <i className={["fa", "fa-random", "shuffle"].join(" ")} onClick={() => {this.shuffleHandler()}}></i>

                            <i className={["fas", "fa-angle-left","previous"].join(" ")} onClick={this.prevsongHandler}></i>

                            {this.state.playing === false ? <i className={["fa", "fa-play", "playpause"].join(" ")} onClick={this.playpauseHandler}></i> : <i className={["fa", "fa-pause", "playpause"].join(" ")} onClick={this.playpauseHandler}></i>}

                            <i className={["fas", "fa-angle-right", "next"].join(" ")} onClick={this.nextsongHandler}></i>

                            <i className={["fas", "fa-undo", "loop"].join(" ")} onClick={this.loopHandler}></i>

                        </div>
                    </div>
                </div>
                <div className="rightSide">
                    {
                        this.props.musicdata === null || this.props.musicdata.length === 0 ? [] : this.props.musicdata.map(item => {
                            return(
                                <div className="musiccard" key={item.id} onClick={() => {this.songHandler(item.id)}}>
                                    <div className="imgsec">
                                        <img src={item.albumCover} alt='' className="miniimage"/>
                                    </div>
                                    <div className="songdetail">
                                        <h3 className="songheading">{item.track}</h3>
                                        <p className="artistheading">{item.artist}</p>
                                    </div>
                                </div>
                            );
                        }) 
                    }
                </div>
            </div>
        )
    }
}
export default Main;
